const parser = require("HDLparser");
const filesys = require("HDLfilesys");

class Default {
    constructor() {}

    async lint(file, options) {
        try {
            let text = filesys.files.readFile(file);
            if (!text) {
                return null;
            }

            let diagnostics = [];
            switch (options.id) {
                case "VLOG":
                case "SVLOG":   
                    diagnostics = []
                break;
                case "VHDL":   
                break;
                default: break;
            }
            return diagnostics;
        } catch (error) {
            console.log(error);
            return [];
        }
    }
}

module.exports = Default;